import groovy.json. JsonSlurper


// Running the Get Token request from Groovy using testRunner and Context

def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context );

def testStep = testRunner.testCase.testSuite.project.getTestSuiteByName("Hours Entry").getTestCaseByName("ADD Modify Remove Single Emp - Single record - sync").getTestStepByName("Access Token")

testStep.run(testRunner, context)


//Reading the accessToken value from response

def GetTokenresponse = context.expand( '${Access Token#Response}' )

log.info GetTokenresponse

def GetTokenJsonResponse = new JsonSlurper()

def json1 = GetTokenJsonResponse.parseText(GetTokenresponse)

def accessToken = json1.access_token

log.info accessToken

testRunner.testCase.testSuite.setPropertyValue("accessToken", accessToken)

import groovy.json.JsonSlurper

def slurper = new JsonSlurper()
def response = context.expand( '${team time card#Response}' )
def jsonResponse = slurper.parseText(response);

def entryDate = context.expand( '2015-12-03' )


def dayEntriesList = jsonResponse.teamTimeCards[0].timeCards[0].dayEntries
for(i=0 ; i < dayEntriesList.size ; i++){
def timeEntries = jsonResponse.teamTimeCards[0].timeCards[0].dayEntries[i].timeEntries
	if(timeEntries != null){
	    	for( j=0; j < timeEntries.size ; j++){   
			 if(timeEntries[j].entryDate == entryDate){
		       	log.info(jsonResponse.teamTimeCards[0].timeCards[0].dayEntries.timeEntries[i])
		       	 entryID = timeEntries[j].entryID.toString();	
		       	 log.info(entryID)
		       	 break;
		   	   } 
    	        }
	  }

}


testRunner.testCase.setPropertyValue("entryID", entryID)

