import groovy.json. JsonSlurper


// Running the Get Token request from Groovy using testRunner and Context

def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context );


def httpStatusCode = context.testCase.testSteps["GET import entry"].testRequest.response.responseHeaders["#status#"]

def statusCode = (httpStatusCode =~ " [1-5]\\d\\d ")[0]

log.info statusCode

if (statusCode.toInteger() == 400 || statusCode.toInteger() == 500){

assert "the status code is Invalid" + statusCode

}else if(statusCode.toInteger() == 201){

log.info "Successfull"
}else{
	while (statusCode.toInteger() == 200)    {

def testStep = testRunner.testCase.testSuite.project.getTestSuiteByName("Hours Entry").getTestCaseByName("ADD Single Emp - Multiple record").getTestStepByName("GET import entry")

testStep.run(testRunner, context)

// Reading status code of GET events call
httpStatusCode = context.testCase.testSteps["GET import entry"].testRequest.response.responseHeaders["#status#"]

statusCode = (httpStatusCode =~ " [1-5]\\d\\d ")[0]

log.info statusCode
Thread.sleep(5000)		
}
}

